    String randomUUID() {
        byte[] randomBytes = new byte[16];
        randomNoGenerator.nextBytes(randomBytes);
        return UUID.nameUUIDFromBytes(randomBytes).toString();
    }

    public PositionData generate() {

        PositionData data = new PositionData();
        SystemBookPair bookPair = randomBookPair();
        String positionId = randomUUID();
        String currency = currencies[randomNoGenerator.nextInt(currencies.length)];
        String decorator = decorators[randomNoGenerator.nextInt(decorators.length)];
        String decoratorId = randomUUID();
        data.setPosition(new KVPair<>(
                new PositionPK(positionId),
                new Position(
                    positionDate,
                    "Trade",
                    currency,
                    bookPair.getBook(),
                    partyIds[randomNoGenerator.nextInt(partyIds.length)],
                    bookPair.getSystem(),
                    "Trade Position",
                    positionId,
                    bookPair.getSystem(),
                    "PHYS_BUYSELL",
                    decorator,
                    decoratorId,
                    bookPair.getBook(),
                    bookPair.getBook().toLowerCase())));
        List <KVPair<SensitivityPK, Sensitivity>> sensitivity = generateSensitivities(positionId);
        data.setS(sensitivity);

        return data;
    }

    /**
     * This method generates a random position
     */
    public KVPair<PositionPK, Position> generatePosition() {
        SystemBookPair bookPair = randomBookPair();
        String positionId = randomUUID();
        String currency = currencies[randomNoGenerator.nextInt(currencies.length)];
        String decorator = decorators[randomNoGenerator.nextInt(decorators.length)];
        String decoratorId = randomUUID();
        return new KVPair<>(
                new PositionPK(positionId),
                new Position(positionDate,
                    "Trade", currency, bookPair.getBook(), partyIds[randomNoGenerator.nextInt(partyIds.length)],
                    bookPair.getSystem(), "Trade Position", positionId,
                    bookPair.getSystem(),"PHYS_BUYSELL", decorator, decoratorId,bookPair.getBook(), bookPair.getBook().toLowerCase()));
    }

    /**
     * This method generates sensitivities for a given position
     */
    public List<KVPair<SensitivityPK, Sensitivity>> generateSensitivities(String pID){
        List <KVPair<SensitivityPK,Sensitivity>> sensitivity = new ArrayList<>(324); //every position produces 324 measures
        double sValue;
        for(String risk : riskTypes)
        {
            for(String gridPoint : gridPoints)
            {
                for(String qualifier : qualifierTypes)
                {
                    sValue = -10000000.0 + (100000000 - 10000000.0) * randomNoGenerator.nextDouble();
                    sensitivity.add(new KVPair<>(
                            new SensitivityPK(pID, sensitivityCount),
                            new Sensitivity("2060051", risk, qualifier, "NA", gridPoint,
                                indexes[randomNoGenerator.nextInt(indexes.length)],sValue, "USD",sValue)));
                    sensitivityCount++;
                }
            }

        }
        return sensitivity;
    }



